es; // if (searchTerm) { // filteredArtistes = lesArtistes.filter((artiste) => // artiste.nomDeSceneMG.toLowerCase().includes(searchTerm.toLowerCase()) // ) // } const [filtreDate, setFiltreDate] = useState(\\"Tout\\"); const convertDateFilter = (filtreDate: { split: (arg0: string) => [any, any]; }) => { const [jour, mois] = filtreDate.split(' '); const moisNumerique = { janvier: '01', février: '02', mars: '03', avril: '04', mai: '05', juin: '06', juillet: '07', août: '08', septembre: '09', octobre: '10', novembre: '11', décembre: '12' }[mois]; return \`{jour}-{moisNumerique}\`; }; let filteredGroupes = lesGroupes; if (searchTerm) { filteredGroupes = filteredGroupes.filter((groupe) => groupe.nomG.toLowerCase().includes(searchTerm.toLowerCase()) ); } if (filtreDate !== \\"Tout\\") { filteredGroupes = filteredGroupes.filter((groupe) => groupe.datePassage.includes(filtreDate) ); } let filteredArtistes = lesArtistes; if (searchTerm) { filteredArtistes = filteredArtistes.filter((artiste) => artiste.nomDeSceneMG.toLowerCase().includes(searchTerm.toLowerCase()) ); } if (filtreDate !== \\"Tout\\") { filteredArtistes = filteredArtistes.filter((artiste) => { const groupeInfo = groupePassageMap.current.get(artiste.idG); return groupeInfo?.datePassage.includes(filtreDate); // Assurez-vous que le format correspond }); } const [filtreAffichage, setFiltreAffichage] = useState(\\"Grille\\"); const [filtreGenre, setFiltreGenre] = useState(\\"Tout\\"); const pageRef = useRef<HTMLDivElement>(null); const contentVariants = { visible: { filter: \\"blur(0px)\\", scale: 1, zIndex: 1, transition: { duration: 0.5, ease: [1, 0, 0, 1], }, }, hidden: { filter: \\"blur(10px)\\", scale: 0.8, zIndex: -1, transition: { duration: 0.5, ease: [1, 0, 0, 1], }, }, }; useEffect(() => { window.scrollTo(0, 0); props.setIsNavTransparent(false); }, []); return ( <> <motion.div id=\\"Programmation\\" className=\\"page-defaut\\" variants={contentVariants} animate={props.isNavInFocus ? \\"hidden\\" : \\"visibl